const coinMarketCap='https://api.coinmarketcap.com/v1/ticker/?limit=10';let app=new Vue({el:'#app',data:{coins:[]},methods:{getCoins:function(){axios.get(coinMarketCap).then(a=>{this.coins=a.data}).catch(a=>{return a})},getCoinImage:function(a){return'https://files.coinmarketcap.com/static/img/coins/16x16/'+a+'.png'},getColor:a=>{return 0<a?'color:green;':'color:red;'},currencyConv:a=>{return a.replace(/(\d)(?=(\d{3})+(?:\.\d+)?$)/g,'$1,')}},created:function(){this.getCoins()}});setInterval(()=>{app.getCoins()},60000);